DROP TABLE IF EXISTS `t_order`;
CREATE TABLE `t_order`  (
  `order_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '订单id',
  `user_id` int(11) NOT NULL COMMENT '用户id',
  `schedule_id` int(11) NOT NULL COMMENT '排片id',
  `number` int(11) NOT NULL COMMENT '购买的电影票数',
  `price` decimal(6, 2) NOT NULL COMMENT '电影票价格',
  `seat` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '座位信息',
  `state` int(11) NOT NULL COMMENT '支付状态(0:未支付，1:已支付，2:已超时，3已退款)',
  `code` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '取票码',
  `date` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '下单时间',
  PRIMARY KEY (`order_id`) USING BTREE,
  INDEX `fk_ou`(`user_id`) USING BTREE,
