DROP TABLE IF EXISTS `t_schedule`;
CREATE TABLE `t_schedule`  (
  `schedule_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '排片id',
  `movie_id` int(11) NOT NULL COMMENT '电影id',
  `cinema_id` int(11) NOT NULL COMMENT '电影院id',
  `hall_id` int(11) NOT NULL COMMENT '影厅id',
  `movie_interval_id` int(11) NOT NULL COMMENT '场次id',
  `date` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '放映时间',
  `language` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '语言版本',
  `price` decimal(5, 2) NOT NULL COMMENT '电影价格',
  `seat` json NOT NULL COMMENT '座位（0代表空位，1代表没有座位，2代表已选择，3代表已售出）',
  PRIMARY KEY (`schedule_id`) USING BTREE,
  INDEX `fk_sm`(`movie_id`) USING BTREE,
  INDEX `fk_sc`(`cinema_id`) USING BTREE,
  INDEX `fk_sh`(`hall_id`) USING BTREE,
  INDEX `fk_smi`(`movie_interval_id`) USING BTREE,
  CONSTRAINT `fk_sc` FOREIGN KEY (`cinema_id`) REFERENCES `t_cinema` (`cinema_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_sh` FOREIGN KEY (`hall_id`) REFERENCES `t_hall` (`hall_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_sm` FOREIGN KEY (`movie_id`) REFERENCES `t_movie` (`movie_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_smi` FOREIGN KEY (`movie_interval_id`) REFERENCES `t_movie_interval` (`movie_interval_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 35 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

